/* Prime Number Checker:
Create a program that checks whether a given number is a prime number.
The program should use a separate function to perform the prime check and return
the result.*/using System;internal class PrimeNumber{    private static bool IsPrime(int number) //for checking prime    {        if (number <= 1)        {            return false;        }        for (int i = 2; i <= Math.Sqrt(number); i++)        {            if (number % i == 0)            {                return false;            }        }        return true;    }    public static void Main()//entry point    {        Console.Write("Enter a number to check if it's prime: ");        int num = int.Parse(Console.ReadLine());        bool result = IsPrime(num);        if (result)        {            Console.WriteLine($"{num} is a prime number.");        }        else        {            Console.WriteLine($"{num} is not a prime number.");        }    }}